// This function sort the array from the lowest value to the larger value.#include <cstddef>void sorting_array (int *array, unsigned const *capasity) {	int next_element = 0;	bool array_sorting = false;	for (size_t i = 0; i < *capasity; ++i) {		if (i == 0) {			array_sorting = true;		}		if ((i < *capasity - 1) & (array[i] > array[i + 1])) {			next_element = array[i + 1];			array[i + 1] = array[i];			array[i] = next_element;			array_sorting = false;		}		if ((i == *capasity - 1) & (array_sorting == false)) {			i = - 1;		}	}}